import "./dashboard.css"
import logo from "./images/logo.svg"
import './horizontalNav.css'
import {FaHistory} from 'react-icons/fa'
import {AiFillPushpin, AiFillHome, AiFillWechat} from 'react-icons/ai'
import {CgProfile} from 'react-icons/cg'
import { Link, withRouter } from "react-router-dom"
import { React, Component } from "react"

class HorizontalNav extends Component{

    handleLogout = (event) => {
        event.preventDefault();

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' }
        };
        fetch('/Titsuite-1.0-SNAPSHOT/api/users/logout', requestOptions).then(res => res.json()).then((data) => {
            console.log(data);
            this.props.logout(); // This is the problem.
            this.props.history.push('/login'); // This one too
        });

    }

    render(){
        return(
        <div>
            <ul class="nav flex-column Nav">
                        <li>
                            <img src={logo} class="img-fluid" alt="Logo" />
                        </li>

                            {(localStorage.getItem('role') === 'freelancer') ? 
                            <Link to="/dashboard" ><li class="nav-item"><AiFillHome /> Dashboard</li></Link>
                             : <Link to="/customer/dashboard" ><li class="nav-item"><AiFillHome/> Dashboard</li></Link>}
                        
                        
                            <Link to="/annonces" ><li class="nav-item"> <AiFillPushpin /> Annonces</li></Link>
                        
                        <li class="nav-item">
                            <a class="nav-link" href="#"> <AiFillWechat /> Service Client</a>
                        </li>
                        <li class="nav-item">
                        {(localStorage.getItem('role') === 'freelancer') ? 
                             <a class="nav-link" href="#"><Link to="/portfeuille" > <FaHistory /> Portfeuille</Link></a>
                             : <a class="nav-link active" href="#"><Link to="/customer/historique" ><FaHistory /> Historique</Link></a>}
                            
                        </li>
                        <li class="nav-item">
                        {(localStorage.getItem('role') === 'freelancer') ? 
                            <a class="nav-link" href="#"><Link to="/freelancer/profile" > <CgProfile /> Profile</Link></a>
                             : <a class="nav-link" href="#"><Link to="/customer/profile" ><CgProfile /> Profile</Link></a>}
                            
                        </li>
                        <div class="BottomNav">
                            <li class="nav-item ">
                            {(localStorage.getItem('role') === 'freelancer') ? 
                            <a class="nav-link" href="#"><Link to="/freelancer/profile" > Hamid Aarif</Link></a>
                             : <a class="nav-link" href="#"><Link to="/customer/profile" > Hamid Aarif</Link></a>}
                    
                            </li>
                            <li class="nav-item ">
                                <button onClick={this.handleLogout} className="btn btn-danger logoutButton">Logout</button>
                            </li>
                        </div>
                    </ul>
        </div>
    )
    }
    
}

export default withRouter(HorizontalNav)
