import { React, Component } from 'react';
import { withRouter } from 'react-router-dom';
import HorizontalNav from './HorizontalNav';


class CustomerProfile extends Component {
    state = {
        email: '',
        firstName: '',
        lastName: '',
        phoneNumber: '',
        birthDate: '',
        city: '',
        address: '',
        subscription: '',
        in_edit: false
    }

    handleProfileChange = (event) => {
        this.setState(
            {
                [event.target.name]: event.target.value
            }
        )
    }

    handleProfileUpdate = (event) => {
        event.preventDefault();

        const user = {
            firstName: this.state.firstName,
            lastName: this.state.lastName,
            phoneNumber: this.state.phoneNumber,
            birthDate: this.state.birthDate,
            city: this.state.city,
            address: this.state.address,
            activity: this.state.activity,
            minimumWage: this.state.minimumWage
        };

        console.log(user);

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(user)
        };
        fetch('/Titsuite-1.0-SNAPSHOT/api/users/profile/update', requestOptions)
            .then(res => res.json())
            .then((data) => {
                console.log(data);
            }
        );
    }

    handleChange = (event) => {
        console.log(event);
        this.setState(
            {
                [event.target.name]: event.target.value
            }
        )
    }

    handleLogout = (event) => {
        event.preventDefault();

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' }
        };
        fetch('/Titsuite-1.0-SNAPSHOT/api/users/logout', requestOptions).then(res => res.json()).then((data) => {
            console.log(data);
            this.props.history.push('/login');
        });
    }

    componentDidMount() {
        const requestOptions = {
            method: 'GET',
            headers: { 'Content-Type': 'application/json' }
        };
        fetch('/Titsuite-1.0-SNAPSHOT/api/users/profile', requestOptions).then(res => res.json()).then((data) => {
            console.log(data);
            if (data != null) {
                console.log(data);
                this.setState(
                    {
                        email: data.email,
                        firstName: data.firstName,
                        lastName: data.lastName,
                        phoneNumber: data.phoneNumber,
                        birthDate: data.birthDate,
                        city: data.city,
                        address: data.address,
                        subscription: data.subscription
                    }
                );
            }
        });
    }

    formatDate = (date) => {
        let d = new Date(date),
        month = '' + (d.getMonth() + 1),
        day = '' + d.getDate(),
        year = d.getFullYear();
    
        if (month.length < 2) 
            month = '0' + month;
        if (day.length < 2) 
            day = '0' + day;
    
        return [year, month, day].join('-');
    }

    render() {
        return (<div className="row">
                    <div className="col-2">
                        <HorizontalNav logout={this.props.rest.logout}/>
                    </div>
                
                    <div className="col-8 Content">
                    <div className="col">
                    <div className="">
                        <div className="">
                            <div className="">
                            <form onSubmit={this.handleProfileUpdate}>
                                <div >
                                    <div className="row">
                                        <div className="col">
                                            <button type="submit" className="btn btn-danger floatRight">Save</button>
                                            <h5 className="freelancerProfile"> Personal Details <br/> {this.state.email} </h5>
                                            <p></p>
                                        </div>
                                    </div>
                                        
                                        <div className="row">
                                            <div className="col-6">
                                                <label>First Name  </label>
                                                <input name="firstName" value={this.state.firstName} onChange={this.handleProfileChange} className="form-control" required />
                                            </div>
                                            <div className="col-6">
                                                <label>Last Name</label>
                                                <input name="lastName" value={this.state.lastName} onChange={this.handleProfileChange} className="form-control" required />
                                            </div>
                                        </div>
                                        <hr />
                                        <div className="row">
                                            <div className="col">
                                                <label>Birth Date</label>
                                                <input name="birthDate" type="date" value={this.formatDate(this.state.birthDate)} onChange={this.handleProfileChange} className="form-control" required />
                                            </div>
                                        </div>

                                        <hr />
                                        <div className="row">
                                            <div className="col-6">
                                                <label>Address</label>
                                                <input name="address" value={this.state.address} onChange={this.handleProfileChange} className="form-control" required />
                                            </div>
                                            <div className="col-6">
                                                <label>City</label>
                                                <input name="city" value={this.state.city} onChange={this.handleProfileChange} className="form-control" required />
                                            </div>
                                        </div>
                                        <hr />
                                        <div className="row">
                                            <div className="col">
                                                <label>Phone Number</label>
                                                <input name="phoneNumber" type="tel" value={this.state.phoneNumber} onChange={this.handleProfileChange} className="form-control" required />
                                            </div>
                                            
                                        </div>
                                        <hr />
                                        <div className="row">
                                            <div className="col-6">
                                                <label>Subscription</label>
                                                <input name="subscription" value={this.state.subscription} onChange={this.handleProfileChange} className="form-control" required />
                                            </div>
                                        </div>
                                      
                                    </div>
                                    </form>  

                                    <div className="Login">
           <div className="container">

                <div className="row">
                    <div className="col illustration2">
                        <h2><center>Tous nos services au bout du doigt</center></h2>
                        <img src={signUp} className="img-fluid" alt="Logo" /> 
                    </div>
                    <div className="col SignupForm">
                        <h3> <center>Complete the form below</center></h3>
                        <form onSubmit={this.handleSubmit}>
                            <div className="form-group">
                                <label for="exampleInputEmail1">Email address</label>
                                <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" name="email" placeholder="Enter email" onChange={this.handleChange} required/>
                        
                            </div>
                            <div className="form-group">
                                <label for="exampleInputPassword1">Password</label>
                                <input type="password" className="form-control" id="exampleInputPassword1"  name="password" placeholder="Password" onChange={this.handleChange} required/>
                            </div>
                            <div className="form-group">
                                <label for="exampleInputPassword1">Repeat your Password</label>
                                <input type="password" className="form-control" id="exampleInputPassword1" placeholder="Repeat your Password" />
                            </div>

                            <div className="form-group">
                                <label for="exampleInputEmail1">Address</label>
                                <input type="texte" className="form-control"   placeholder="Address" name="address" onChange={this.handleChange}/>
                            </div>
                            
                            <div className="row">
                                <div className="col form-group">
                                    <label for="exampleInputEmail1">City</label>
                                    <input className="form-control" type="text" placeholder="City" name="city" onChange={this.handleChange} />
                                </div>
                                <div className="col form-group">
                                    <label for="exampleInputEmail1">Phone Number</label>
                                    <input className="form-control" type="text" placeholder="Phone number" name="phoneNumber" onChange={this.handleChange}/>
                                </div>
                            </div>
                            <div className="form-check">
                                    <input className="form-check-input" type="radio" name="role" onChange={this.setRole} id="exampleRadios1" value="customer"  onChange={this.handleChange} checked={this.state.role === "customer"}  />
                                    <label className="form-check-label" for="exampleRadios1">
                                        I’m a client, and I have a job that needs to be done.
                                    </label>
                                </div>
                                <div className="form-check">
                                    <input className="form-check-input" type="radio" name="role" id="exampleRadios2" value="freelancer" onChange={this.handleChange} checked={this.state.role === "freelancer"} />
                                    <label className="form-check-label" for="exampleRadios2">
                                        I’m a freelancer, and I want to showcase my services.
                                    </label>
                                </div>
                            <br />
                            <span className="signupButton"><button type="submit" className="btn btn-primary btn-lg btn-block" >Sign Up</button></span>
                        </form>
                    </div>
                    
                </div>

                <div className="row">
                    
                    <div className="col">
                        
                    </div>
                    
                </div>
            </div>
        </div>
                            </div>
                        </div>
                    </div>
                    </div>

                    </div>
                    

                
                </div>
                
        );
    }
}

export default withRouter(CustomerProfile);
